function translateText(){var e,t=document.getElementById("inputText").value.toLowerCase(),n=document.getElementById("translationDirection").value;"en-to-koshi"===n?e=generatePseudoTranslation(t):"koshi-to-en"===n&&(e=reversePseudoTranslation(t)),document.getElementById("translationResult").textContent="Translated Text: "+e}function generatePseudoTranslation(e){for(var t="",n=0;n<e.length;n++){var r,a=e.charCodeAt(n);a>=97&&a<=122?(r=a+1)>122&&(r=97):r=a,t+=String.fromCharCode(r)}return t=t.replace(/a/g,"ä").replace(/o/g,"ö").replace(/e/g,"å")}function reversePseudoTranslation(e){var t="";e=e.replace(/ä/g,"a").replace(/ö/g,"o").replace(/å/g,"e");for(var n=0;n<e.length;n++){var r,a=e.charCodeAt(n);a>=97&&a<=122?(r=a-1)<97&&(r=122):r=a,t+=String.fromCharCode(r)}return t}document.getElementById("translateBtn").addEventListener("click",translateText);
